import React from "react";
import {
  FormItemGender,
  FormItemFullName,
  FormItemPhone,
  FormItemMail,
} from "../../Shared/Input";
import { ButtonSave, ButtonSend } from "../../Shared/Button";
import { Form, Input, Divider, Space, Tag } from "antd";

export const AddForm = ({ loading, onFinish }) => (
  <Form onFinish={onFinish}>
    <FormItemGender />
    <FormItemFullName />
    <FormItemPhone />
    <FormItemMail />
    <ButtonSave type="primary" htmlType="submit" loading={loading} />
  </Form>
);

const { TextArea } = Input;

export const ContactForm = ({ onFinish, initialValues, messages }) => {
  const [current, setCurrent] = useState(null);
  return (
    <Form onFinish={onFinish} initialValues={initialValues}>
      <FormItemFullName />
      <TextArea rows={1} />
      <Divider />
      <Space>
        {messages.map((message) => (
          <Tag>{message}</Tag>
        ))}
      </Space>
      <ButtonSend type="primary" htmlType="submit" />
    </Form>
  );
};
