import React from "react";
import { List, Card, Avatar, Descriptions } from "antd";
import { MailOutlined, PhoneOutlined } from "@ant-design/icons";

export const ListCustomer = ({ data, onSelected }) => {
  return (
    <List
      grid={{ xs: 1 }}
      size="small"
      itemLayout="horizontal"
      dataSource={data}
      style={{ margin: 15, overflow: "auto", height: 500 }}
      bordered={false}
      renderItem={(customer: any) => (
        <List.Item>
          <Card onClick={() => onSelected(customer)} hoverable>
            <List.Item.Meta title={customer.fullName} />
            <List.Item.Meta title={customer.phone} />
          </Card>
        </List.Item>
      )}
    />
  );
};

export const Display = ({ fullName, mail, phone }) => {
  return (
    <Card
      cover={
        <img
          src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
          height={50}
          width={50}
        />
      }
      title={fullName}
    >
      <Descriptions column={1} style={{ marginTop: 20 }}>
        <Descriptions.Item label={<MailOutlined />}>{mail}</Descriptions.Item>
        <Descriptions.Item label={<PhoneOutlined />}>{phone}</Descriptions.Item>
      </Descriptions>
    </Card>
  );
};
