import React, { useState } from "react";
import { Checkbox, Button } from "antd";
import { CheckCircleOutlined } from "@ant-design/icons";

export const FollowForm = ({ loading, onFinish, isCut, isOver, isStarded }) => {
  const [cut, setCut] = useState(isCut);
  const [over, setOver] = useState(isOver);
  const [start, setStart] = useState(isStarded);
  return (
    <div>
      <Checkbox
        checked={start}
        onChange={({ target: { checked } }) => setStart(checked)}
      >
        Démarré
      </Checkbox>
      <Checkbox
        disabled={!start}
        checked={cut}
        onChange={({ target: { checked } }) => setCut(checked)}
      >
        Coupé
      </Checkbox>
      <Checkbox
        disabled={!cut || !start}
        checked={over}
        onChange={({ target: { checked } }) => setOver(checked)}
      >
        Termininé
      </Checkbox>

      <Button
        type="primary"
        loading={loading}
        onClick={() => onFinish({ isCut: cut, isOver: over, isStarded: start })}
        icon={<CheckCircleOutlined />}
      >
        Valider
      </Button>
    </div>
  );
};
