import React, { useContext, useState } from "react";
import { Theme } from "../../Shared/Layout";
import { Row, Col, PageHeader, message, List, Button, Skeleton } from "antd";
import { UserContext } from "../../Context";
import { PlusCircleOutlined } from "@ant-design/icons";
import { useQuery } from "@apollo/react-hooks";
import { CUSTOMERSBYWORKSHOPID } from "../../../Gql/Query";
import { Display } from "./Display";


export const Customer = () => {
  const { user } = useContext(UserContext);
  const { data, loading, refetch } = useQuery(CUSTOMERSBYWORKSHOPID, {
    variables: { workshopId: user.workshop.id, all:true },
  });

  if(data)
    console.log(data)

  return (
    <Theme>
       <List
              rowKey="id"
              grid={{ gutter: 8, xs: 1, sm: 1, md: 1, lg: 2, xl: 3 }}
              dataSource={data?.customers.nodes}
              renderItem={(customer:any, key) => {
                return (
                  <List.Item key={key}>
                    <Display
                        {...customer}
                    />
                  </List.Item>
                );
              }}
            />
    </Theme>
  );
};
