import React, { useState } from "react";
import {
  FormItemGender,
  FormItemFullName,
  FormItemPhone,
  FormItemMail,
} from "../../Shared/Input";
import { ButtonSave, ButtonSend } from "../../Shared/Button";
import { Form, Input, Divider, Space, Tag, Select } from "antd";
import Search from "antd/lib/transfer/search";

const { Option } = Select;

export const AddForm = ({ loading, onFinish }) => (
  <Form onFinish={onFinish}>
    <FormItemGender />
    <FormItemFullName />
    <FormItemPhone />
    <FormItemMail />
    <ButtonSave type="primary" htmlType="submit" loading={loading} />
  </Form>
);

const { TextArea } = Input;

export const ContactForm = ({ onFinish, initialValues, messages }) => {
  const [current, setCurrent] = useState(null);

  const onChange = ({ target: { value } }) => {
    setCurrent({ messageId: null, message: value });
  };

  return (
    <Form onFinish={onFinish} initialValues={initialValues}>
      <FormItemFullName />
      <Select defaultValue="" style={{ width: 200 }}>
        <Option value="commande">Commande Prete</Option>
        <Option value="nouveaute">Informer des Nouveaut√©s</Option>
      </Select>
      <Divider />
      <ButtonSend type="primary" htmlType="submit" onFinish={onFinish} />
    </Form>
  );
};

export const FilterForm = ({ onSearch }) => {
  return (
    <Form>
      <Input.Search
        placeholder="Rechercher un client"
        enterButton="Search"
        size="large"
        onSearch={onSearch}
      />
    </Form>
  );
};
