import { Modal } from "antd";
import React, { useContext } from "react";
import { ContactForm } from "./Form";
import { MESSAGEBYSUBJECT } from "../../../Gql/Query";
import { useQuery } from "@apollo/react-hooks";
import { UserContext } from "../../Context";

export const Contact = ({
  customer,
  visible,
  onCancel,
  onSuccess,
  onError,
}) => {
  const { data, loading } = useQuery(MESSAGEBYSUBJECT, {
    variables: { subject: "Info" },
  });
  const { user } = useContext(UserContext);
  const onFinish = (values) => {
    console.log(values);
  };
  return (
    <Modal visible={visible} onCancel={onCancel}>
      <ContactForm
        onFinish={onFinish}
        initialValues={customer}
        messages={data?.messages.nodes.map(({ message }) =>
          message
            .replace("{gender}", customer.gender)
            .replace("{fullName}", customer.fullName)
            .replace("{workshopName}", customer.fullName)
        )}
      />
    </Modal>
  );
};
