import React, { useState } from "react";
import { Card, Input, Modal, Tag, Descriptions } from "antd";
import { NumberOutlined, CloseCircleOutlined } from "@ant-design/icons";
import { useMutation } from "@apollo/react-hooks";
import { UPDATESERVICEBYCODE } from "../../../Gql/Mutation";
import { ServiceByCode } from "../Service/ServiceByCode";
import { DeliverForm } from "./Form";

export const Index = ({ visible, onCancel, onSuccess, onError }) => {
  const [code, setCode] = useState("");
  const [service, setService] = useState();

  return (
    <Modal
      visible={visible}
      onCancel={onCancel}
      footer={null}
      closeIcon={
        <CloseCircleOutlined style={{ fontSize: 20, color: "#1DA57A" }} />
      }
    >
      <Card bordered={false} title="Livrer un service">
        <Input.Search
          style={{ marginBottom: 10 }}
          prefix={<NumberOutlined />}
          onSearch={setCode}
          enterButton="Ok"
          placeholder="Code du service"
        />
        {code && <ServiceByCode onFind={setService} code={code} />}
        {service && (
          <Deliver
            code={code}
            onError={onError}
            onSuccess={onSuccess}
            service={service}
          />
        )}
      </Card>
    </Modal>
  );
};

const Deliver = ({ code, service, onSuccess, onError }) => {
  const [editService, { loading }] = useMutation(UPDATESERVICEBYCODE);

  const onFinish = (values) => {
    editService({
      variables: {
        code: parseInt(code),
        patch: { isDelivered: true, ...values },
      },
    })
      .then(({ data }) => {
        onSuccess(data);
      })
      .catch((error) => onError(error));
  };
  return (
    <Card bordered={false}>
      <Descriptions title="Infos sur le service" column={1}>
        <Descriptions.Item>
          <Tag color="#1DA57A">{`Prix: ${service.price} CFA`}</Tag>
        </Descriptions.Item>
        <Descriptions.Item>
          <Tag color="#1DA57A">{`1er Versement: ${service.avance} CFA`}</Tag>
        </Descriptions.Item>
        <Descriptions.Item>
          <Tag color="#1DA57A">{`Montant restant: ${
            service.price - service.avance
          } CFA`}</Tag>
        </Descriptions.Item>
      </Descriptions>
      <DeliverForm
        loading={loading}
        onFinish={onFinish}
        initialValues={service}
      />
    </Card>
  );
};
