import React from "react";
import {
  FormItemGender,
  FormItemFullName,
  FormItemPhone,
  FormItemMail,
} from "../../Shared/Input";
import { ButtonSave, ButtonSend } from "../../Shared/Button";
import { Form, Input, Divider } from "antd";

export const AddForm = ({ loading, onFinish }) => (
  <Form onFinish={onFinish}>
    <FormItemGender />
    <FormItemFullName />
    <FormItemPhone />
    <FormItemMail />
    <ButtonSave type="primary" htmlType="submit" loading={loading} />
  </Form>
);

const { TextArea } = Input;

export const ContactForm = ({ onFinish }) => {
  return (
    <Form onFinish={onFinish}>
      <FormItemFullName />
      <TextArea rows={1} value={current?.message} onChange={onchange} />
      <Divider />
      <Space direction="vertical">
        {data?.messages.nodes.map(({ id, message }) => (
          <Tag
            key={id}
            color="#1DA57A"
            style={{ cursor: "pointer", width: "100%", borderRadius: 5 }}
            onClick={() => setCurrent({ messageId: id, message })}
          >
            {message}
          </Tag>
        ))}
      </Space>
      <ButtonSend type="primary" htmlType="submit" />
    </Form>
  );
};
