import React from "react";
import { List, Card, Avatar, Descriptions } from "antd";
import { MailOutlined, PhoneOutlined } from "@ant-design/icons";
import Male from "../../../Male.svg";
import Female from "../../../Female.svg";

export const ListCustomer = ({ data, onSelected }) => {
  return (
    <List
      grid={{ xs: 1 }}
      size="small"
      itemLayout="horizontal"
      dataSource={data}
      style={{ margin: 15, overflow: "auto", height: 500 }}
      bordered={false}
      renderItem={(customer: any) => (
        <List.Item>
          <Card onClick={() => onSelected(customer)} hoverable>
            <List.Item.Meta title={customer.fullName} />
            <List.Item.Meta title={customer.phone} />
          </Card>
        </List.Item>
      )}
    />
  );
};

export const Display = ({ fullName, mail, phone, gender }) => {
  return (
    <List.Item>
      <List.Item.Meta
        avatar={
          <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
        }
        title={fullName}
      />
      <List.Item.Meta
        title={
          <span>
            <MailOutlined /> Email
          </span>
        }
        description={mail}
      />
      <List.Item.Meta
        title={
          <span>
            <PhoneOutlined /> Telephone
          </span>
        }
        description={phone}
      />
    </List.Item>
  );
};
