import React, { useState } from "react";
import { Card, Input, Modal } from "antd";
import { NumberOutlined, CloseCircleOutlined } from "@ant-design/icons";
import { useMutation } from "@apollo/react-hooks";
import { UPDATESERVICEBYCODE } from "../../../Gql/Mutation";
import { ServiceByCode } from "../Service/ServiceByCode";
import { FollowForm } from "./Form";

export const Index = ({ visible, onCancel, onSuccess, onError }) => {
  const [code, setCode] = useState("");
  const [service, setService] = useState();

  const onFind = (service) => {
    setService(service);
  };

  return (
    <Modal
      visible={visible}
      onCancel={onCancel}
      footer={null}
      closeIcon={
        <CloseCircleOutlined style={{ fontSize: 20, color: "#1DA57A" }} />
      }
    >
      <Card bordered={false}>
        <Input.Search
          style={{ marginBottom: 10 }}
          prefix={<NumberOutlined />}
          onSearch={setCode}
          enterButton="Ok"
          placeholder="Code du service"
        />
        {code && <ServiceByCode code={code} onFind={onFind} />}
        {service && (
          <Follow
            code={code}
            service={service}
            onSuccess={onSuccess}
            onError={onError}
          />
        )}
      </Card>
    </Modal>
  );
};

const Follow = ({ code, service, onSuccess, onError }) => {
  const [editService, { loading }] = useMutation(UPDATESERVICEBYCODE);

  const onFinish = (values) => {
    editService({
      variables: {
        code: parseInt(code),
        patch: { isDelivered: true, ...values },
      },
    })
      .then(({ data }) => {
        onSuccess(data);
      })
      .catch((error) => onError(error));
  };

  return (
    <Card bordered={false}>
      <FollowForm onFinish={onFinish} {...service} loading={loading} />
    </Card>
  );
};
