import { Modal, message } from "antd";
import React, { useContext } from "react";
import { ContactForm } from "./Form";
import { MESSAGEBYSUBJECT } from "../../../Gql/Query";
import { useQuery } from "@apollo/react-hooks";
import { UserContext } from "../../Context";
import { ContactClienBytMail } from "../../../Service";

export const Contact = ({
  customer,
  visible,
  onCancel,
  onSuccess,
  onError,
}) => {
  const { data, loading } = useQuery(MESSAGEBYSUBJECT, {
    variables: { subject: "Info" },
  });

  const { user } = useContext(UserContext);
  const onFinish = (values) => {
    ContactClienBytMail({
      mail: customer.mail,
      message: "",
      subject: "",
    });

    console.log(values);
  };

  return (
    <Modal
      visible={visible}
      onCancel={onCancel}
      bodyStyle={{ margin: 20 }}
      width={600}
      footer={[
        <ButtonSend type="primary" htmlType="submit" onFinish={onFinish} />,
        <ButtonCancel />,
      ]}
      title="Contacter vos clients"
    >
      <ContactForm
        onFinish={onFinish}
        initialValues={customer}
        messages={data?.messages.nodes.map(({ id, message }) => ({
          id: id,
          message: message

            .replace("{fullName}", customer?.fullName)
            .replace("{workshopName}", user.workshop.name),
        }))}
      />
    </Modal>
  );
};
