import React, { useState } from "react";
import {
  FormItemGender,
  FormItemFullName,
  FormItemPhone,
  FormItemMail,
} from "../../Shared/Input";
import { ButtonSave, ButtonSend } from "../../Shared/Button";
import { Form, Input, Divider, Space, Tag, Select } from "antd";

const { Option } = Select;
export const AddForm = ({ loading, onFinish }) => (
  <Form onFinish={onFinish}>
    <FormItemGender />
    <FormItemFullName />
    <FormItemPhone />
    <FormItemMail />
    <ButtonSave type="primary" htmlType="submit" loading={loading} />
  </Form>
);

const { TextArea } = Input;

export const ContactForm = ({ onFinish, initialValues, messages }) => {
  const [current, setCurrent] = useState(null);
  const onChange = ({ target: { value } }) => {
    setCurrent({ messageId: null, message: value });
  };
  return (
    <Form onFinish={onFinish} initialValues={initialValues}>
      <FormItemFullName />

      <TextArea rows={1} />
      {/* <Divider />
      <Space direction="vertical">
        {messages &&
          messages.map(({ id, message }) => (
            <Tag
              key={id}
              color="#1DA57A"
              style={{ cursor: "pointer", width: "100%", borderRadius: 5 }}
              onClick={() => setCurrent({ messageId: id, message })}
            >
              {message}
            </Tag>
          ))}
      </Space> */}
      <Select defaultValue="lucy" style={{ width: 120 }}>
        <Option value="jack">Jack</Option>
        <Option value="lucy">Lucy</Option>
        <Option value="Yiminghe">yiminghe</Option>
      </Select>
      <Divider />
      <ButtonSend type="primary" htmlType="submit" />
    </Form>
  );
};
