import React, { useContext, useState } from "react";
import { Theme } from "../../Shared/Layout";
import {
  Row,
  Col,
  PageHeader,
  message,
  List,
  Button,
  Skeleton,
  Card,
} from "antd";
import { UserContext } from "../../Context";
import { PlusCircleOutlined } from "@ant-design/icons";
import { useQuery } from "@apollo/react-hooks";
import { CUSTOMERSBYWORKSHOPID } from "../../../Gql/Query";
import { Display } from "./Display";
import { FilterForm } from "./Form";
import { Contact as ContactModal } from "./Contact";
import { useModal } from "../../../hooks/useModal";

export const Customer = () => {
  const { user } = useContext(UserContext);
  const { data, loading, refetch } = useQuery(CUSTOMERSBYWORKSHOPID, {
    variables: { workshopId: user.workshop.id, all: true },
  });
  const [current, setCurrent] = useState();
  const [visible, toggle] = useModal();

  const onContact = (customer) => {
    setCurrent(customer);
    toggle();
  };
  const onSearch = (value) => {};

  return (
    <Theme>
      <ContactModal
        customer={current}
        visible={visible}
        onCancel={toggle}
        onSuccess={() => {}}
        onError={() => {}}
      />
      <Card>
        <FilterForm onSearch={onSearch} />
      </Card>
      <Card>
        <List
          rowKey="id"
          dataSource={data?.customers.nodes}
          renderItem={(customer: any, key) => {
            return (
              <Display
                {...customer}
                numberServicesByYear={
                  data?.numberServicesByYears.nodes[0].count
                }
                onContact={onContact}
              />
            );
          }}
        />
      </Card>
    </Theme>
  );
};
